mapper.select:
SELECT 

FROM detection_record r
LEFT JOIN detection_type t ON r.detection_type_id = t.id
WHERE r.user_id = #{userId}
AND r.detection_time BETWEEN #{startTime} AND #{endTime}
ORDER BY r.detection_time DESC

SELECT COUNT(*)
FROM detection_record
WHERE user_id = #{userId}

mapper.insert:
INSERT INTO detection_record (
    user_id, detection_type_id, file_url, file_name, file_size, content, result,
    confidence, detection_time, status, error_message
)
VALUES (
    #{userId}, #{detectionTypeId}, #{fileUrl}, #{fileName}, #{fileSize}, #{content}, #{result},
    #{confidence}, #{detectionTime}, #{status}, #{errorMessage}
)

mapper.update:
UPDATE detection_record
result = #{result}, confidence = #{confidence}, status = #{status}, error_message = #{errorMessage},
WHERE id = #{id}
