mapper.insert:
INSERT INTO user_quota (
    user_id, total_quota, used_quota, remaining_quota, daily_free_quota, last_free_quota_reset
) VALUES (
    #{userId}, #{totalQuota}, #{usedQuota}, #{remainingQuota}, #{dailyFreeQuota}, #{lastFreeQuotaReset}
)

mapper.update:
UPDATE user_quota
total_quota = #{totalQuota}, used_quota = #{usedQuota}, remaining_quota = #{remainingQuota}, daily_free_quota = #{dailyFreeQuota}, last_free_quota_reset = #{lastFreeQuotaReset},
WHERE user_id = #{userId}

UPDATE user_quota
SET used_quota = used_quota + #{quotaAmount},
    remaining_quota = remaining_quota - #{quotaAmount}
WHERE user_id = #{userId} AND remaining_quota >= #{quotaAmount}
